import React, { Component } from 'react'
import Link from 'next/link'

export default class Layout extends Component {
  render() {
    return (
      <div className="root">
        <headers>
            <Link href="/">
                <a>Home</a>
            </Link>
            <Link href="/about">
                <a>About</a>
            </Link>
            <Link href="/hirme">
                <a>Hirme</a>
            </Link>
        </headers>
        <h1>{this.props.title}</h1>
        {this.props.children}  
        <footer>
            <h3>DurgaPrasad&copy; {new Date().getFullYear()+'-'+ new Date().getMonth()+'-'+new Date().getDate()}</h3>
        </footer>
        <style jsx>{`
            .root{
                display: flex;
                justify-content: center;
                align-items: center;
                flex-direction: column;
            }
            headers
            {
                width:100%;
                display: flex;
                justify-content: space-arround;
                padding : 10px;
                font-size:1.2rem;
                background:orange;
            }
            headers a
            {
                padding: 8px;
                text-decoration: none;
                color:brown;
            }

        `}</style>
      </div>
    )
  }
}
